mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT board_idx, board_writer, board_title, board_content, board_regdate FROM board
ORDER BY board_idx DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE board
SET board_title=#{board_title}, board_content=#{board_content}, board_image=#{board_image}
WHERE board_idx=#{board_idx}
