mapper.select:
SELECT *
FROM 
	(SELECT result1.*, ROWNUM AS rnum
	FROM 
		(SELECT item_idx, item_title, item_price, item_content, item_image, item_category_top_idx, item_category_top_ref
		FROM item 


				WHERE item_category_top_idx = #{item_category_top_idx}


				WHERE item_category_top_ref = #{item_category_top_ref}


				WHERE item_category_top_idx = #{item_category_top_idx}


				WHERE item_category_top_ref = #{item_category_top_ref}


				WHERE item_category_top_idx = #{item_category_top_idx}


				WHERE item_category_top_ref = #{item_category_top_ref}


				WHERE item_category_top_idx = #{item_category_top_idx}


				WHERE item_category_top_ref = #{item_category_top_ref}



	ORDER BY item_idx DESC) result1)

WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE item
SET item_stock=item_stock-1
where item_idx=#{item_idx}

mapper.select:
SELECT item_price
FROM item
WHERE item_idx=#{item_idx}

mapper.update:
UPDATE item
SET item_title=#{item_title}, item_price=#{item_price}, item_stock=#{item_stock}, item_content=#{item_content}, item_image=#{item_image}
WHERE item_idx=#{item_idx}
