mapper.select:
SELECT item_idx, item_title, item_price, item_content, item_image
FROM item i

INNER JOIN item_category_top t
ON i.item_category_top_idx=t.item_category_top_idx


		WHERE i.item_category_top_idx = #{i.item_category_top_idx}



		WHERE #{i.item_category_top_idx} < 11



		WHERE i.item_category_top_idx = #{i.item_category_top_idx}


		WHERE i.item_category_top_idx between 12 and 23


		WHERE i.item_category_top_idx = #{item_category_top_idx}


		WHERE i.item_category_top_idx between 25 and 32


		WHERE i.item_category_top_idx = #{item_category_top_idx}


		WHERE i.item_category_top_idx between 33 and 38

mapper.update:
UPDATE item
SET item_stock=item_stock-1
where item_idx=#{item_idx}

mapper.select:
SELECT item_price
FROM item
WHERE item_idx=#{item_idx}
